<script type="text/javascript">
    $(document).ready(function () {
        $("#comment-block").on("click", "#message-er", function() {
            $("#message-er").css("display", "none");
        });      
    });
    var topScrollCmtButton = topScrollCmtButton || 525;
    function scrollCmtButton(){
        var docHeight = $(document).height();
        var winHeight = $(window).height();
        var allPath = docHeight-winHeight;
        var cmtPath = winHeight-topScrollCmtButton-660;
        var top =topScrollCmtButton+scrollY/allPath*cmtPath;
        $("#comment-button").css("top",top);            
    }
    
    $(\'#sendmsg\').on(\'click\',function(){       
        var data = {};
        data[\'fio\']=$("#cmt_fio").val();
        data[\'mail\']=$("#cmt_mail").val();
        data[\'content\']=$("#cmt_content").val();
        if (Validate(data)) {
            data[\'location\']=window.location.toString();
            $.post("http://investvko.kz/app/ajax/recall",data,function(msg){
                var res = JSON.parse(msg);
                var cnt = "Спасибо, Ваш отзыв принят!";
                if (res.result===\'wait\'){
                    cnt = "Оставлять отзыв можно не чаще чем раз в 2 минуты.";
                }
                if (res.result===\'error\'){
                    cnt = "Ошибка при сохранении вашего отзыва, перезагрузите страницу и попробуйте сново.";
                }
                $("#comment-block").hide();
                $("#comment-msg").html(cnt);
                $("#comment-msg").show();
                return false;
            })
    	}
    });
    
    $(\'#comment-button-close\').on(\'mousedown\',function(){
        $(\'#comment-button\').animate({right:"-242px"});
         $("#message-er").css("display", "none");
        return false;
    });
    $(\'#comment-button\').on(\'click\',function(){
        var lft=$(this).css(\'right\');
        if (lft !=="0px"){
            $("#cmt_fio").val("");
            $("#cmt_mail").val("");
            $("#cmt_content").val("");
            
            $("#comment-msg").hide();
            $("#comment-block").show();        
            $(this).animate({right:"0px"});
        }
    });
    
    $(window).scroll(function(){
        scrollCmtButton();
    });
    $("#comment-button").css("top",topScrollCmtButton);
    
    function Validate(data) {
        $("#message-er").css("display", "none");
        var success = true;
        var error= "";
        if (data[\'fio\'].length == 0)
        {
            error+="Введите ваше имя<br/>";
            success = false;
        }
        
        if (data[\'mail\'].length == 0)
        {
            error+="Введите ваш  Email<br/>";
            success = false;
        }
        else
        {
            var email=data[\'mail\'];
            var reg= new RegExp("[0-9a-z_]+@[0-9a-z_^.]+\\\\.[a-z]{2,3}", \'i\')
            if (!reg.test(email)) {
                error+="Введённый Email не корректен<br/>";
                success = false;
            }
        }
        
        if (data[\'content\'].length == 0)
        {
            error+="Введите текст отзыва<br/>";
            success = false;
        }
        if (!success) {
            $("#message-er .popover-content").html(error);
            $("#message-er").css("display", "block");
        }
        return success;
    }
</script>
